import './App.css';
import { useState } from 'react';
import Navbar from './NavBar';
import Forecast from './Forecast';

function App() {
  const [temperatureData, setTemperatureData] = useState(null);

  const handleDataFetch = async (data) => {
    try {
      const response = await fetch('http://localhost:3001/api/receive_data', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      });

      if (!response.ok) {
        throw new Error('Failed to fetch data');
      }

      const responseData = await response.json();
      setTemperatureData(responseData.hourly);

    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };
  return (
    <div className="App">
      <Navbar onFetchData={handleDataFetch} />
      <div className="content">
        {temperatureData && <Forecast temperatureData={temperatureData} />}
      </div>
    </div>
  );
}

export default App;
