import { Line } from 'react-chartjs-2';
import React, { useEffect, useRef, useMemo } from 'react';

const TemperatureChart = React.memo(({ data }) => {
  console.log("Data time lables: ", data.hourly.time)
  console.log("Data Temperature lables: ", data.hourly.temperature_2m)
  const formattedDates = data.hourly.time.map(dateString => new Date(dateString));
  
  const chartData = useMemo(() => ({
    labels: formattedDates,
    datasets: [
      {
        label: 'Temperature',
        data: data.hourly.temperature_2m,
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
      }
    ]
  }), [data.hourly.temperature_2mm, formattedDates]);

  const chartOptions = {
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'hour', // Specify the time unit (e.g., hour, day, month)
        },
        title: {
          display: true,
          text: 'Time',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Temperature',
        },
      },
    },
  };

  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      chartRef.current.destroy();
    }

    chartRef.current = new Line(chartRef.current, {
      data: chartData,
      options: chartOptions
    });

    return () => {
      if (chartRef.current) {
        chartRef.current.destroy();
      }
    };
  }, [chartData, chartOptions]); // Ensure effect runs only when data changes
  
    return (
      <div>
        <h2>Temperature Chart</h2>
        <canvas ref={chartRef}></canvas>
      </div>
    );
  });

export default TemperatureChart;
