import { Line } from 'react-chartjs-2';

const TemperatureChart = ({ data }) => {
  console.log("Data time lables: ", data.hourly.time)
  console.log("Data Temperature lables: ", data.hourly.temperature_2m)
  const formattedDates = data.hourly.time.map(dateString => new Date(dateString));
  const chartData = {
    labels: formattedDates,
    datasets: [
      {
        label: 'Temperature',
        data: data.hourly.temperature_2m,
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
      }
    ]
  };

  const chartOptions = {
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'hour', // Specify the time unit (e.g., hour, day, month)
        },
        title: {
          display: true,
          text: 'Time',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Temperature',
        },
      },
    },
  };


  return (
    <div>
      <h2>Temperature Chart</h2>
      <Line data={chartData} options={chartOptions} />
    </div>
  );
};

export default TemperatureChart;
